SampleCode.Old:
#include "itkColorTable.h"
include "itkColorTable.h"
typedef itk::ColorTable ColorTableType;
ColorTableType::Pointer colors = ColorTableType::New();
colors->useHeat();
colors->useGray();
colors->useDiscrete();

SampleCode.New:
#include "itkColorTable.h"
include "itkColorTable.h"
typedef itk::ColorTable ColorTableType;
ColorTableType::Pointer colors = ColorTableType::New();
colors->UseHeatColors();
colors->UseGrayColors();
colors->UseDiscreteColors();
